import React, { useState } from 'react';
import FormSignUp from './FormSignUp';
import FormSuccess from './FormSuccess';
import './Form.css'


const Form = () => {
    const [isSubmitted, setIsSubmitted] = useState(false);

    function submitForm(){
        setIsSubmitted(true);
    }
    return (
        
        <>
        <div>
            {!isSubmitted ? <FormSignUp submitForm = {submitForm} /> :
             <FormSuccess/>}
        </div>
        </>
    )
}

export default Form
